import React from 'react';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import EditProfilePopup from './EditProfilePopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import api from '../utils/api.js';
import '../page/index.css';

export default function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [userName, setUserName] = React.useState(null);
  const [userDescription, setUserDescription] = React.useState(null);
  const [userAvatar, setUserAvatar] = React.useState(null);

  api.getUserInfo().then(result=> {
    setUserName(result.name);
    setUserDescription(result.about);
    setUserAvatar(result.avatar);
  });

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  };

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  };

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  };

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsAddPlacePopupOpen(false);
  };

  return (
    <body className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        userName={userName} />
      <Footer />
      <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      />
      <AddPlacePopup
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      />
      <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      />

      <div className="popup popup_type_profile">
        <div className="popup__container">
          <button
            className="popup__close"
            aria-label="закрыть popup"
            type="button"
          />
          <h2 className="popup__title">Редактировать профиль</h2>
          <form className="form" name="formProfile" noValidate="">
            <label className="form__label">
              <input
                id="input_name"
                className="form__input form__input_data_name"
                type="text"
                placeholder="Имя Фамилия"
                name="name"
                minLength={2}
                maxLength={40}
                required=""
              />
              <span className="form__error form__error_input_name" />
            </label>
            <label className="form__label">
              <input
                id="input_about"
                className="form__input form__input_data_job"
                type="text"
                placeholder="Работа"
                name="about"
                minLength={2}
                maxLength={200}
                required=""
              />
              <span className="form__error form__error_input_about" />
            </label>
            <button type="submit" className="form__button">
              Сохранить
            </button>
          </form>
        </div>
      </div>
      <div className="popup popup_type_place">
        <div className="popup__container">
          <button
            className="popup__close"
            aria-label="закрыть popup"
            type="button"
          />
          <h2 className="popup__title">Новое место</h2>
          <form className="form" name="formPlace" noValidate="">
            <label className="form__label">
              <input
                id="input_place"
                className="form__input form__input_data_place"
                type="text"
                placeholder="Название"
                name="name"
                minLength={2}
                maxLength={30}
                required=""
              />
              <span className="form__error form__error_input_place" />
            </label>
            <label className="form__label">
              <input
                id="input_url"
                className="form__input form__input_data_url"
                type="url"
                placeholder="Ссылка на картинку"
                name="link"
                required=""
              />
              <span className="form__error form__error_input_url" />
            </label>
            <button type="submit" className="form__button">
              Создать
            </button>
          </form>
        </div>
      </div>
      <div className="popup popup_type_image">
        <div className="popup__container-image">
          <button
            className="popup__close"
            aria-label="закрыть popup"
            type="button"
          />
          <img src="#" alt="" className="popup__image" />
          <p className="popup__description" />
        </div>
      </div>
      <div className="popup popup_type_delete-card">
        <div className="popup__container">
          <button
            className="popup__close"
            aria-label="закрыть popup"
            type="button"
          />
          <h2 className="popup__title">Вы уверены?</h2>
          <form className="form" name="formDeleteCard" action="#" noValidate="">
            <button type="submit" className="form__button">
              Да
            </button>
          </form>
        </div>
      </div>
      <div className="popup popup_type_avatar">
        <div className="popup__container">
          <button
            className="popup__close"
            aria-label="закрыть popup"
            type="button"
          />
          <h2 className="popup__title">Обновить аватар</h2>
          <form className="form" name="formAvatar" action="#" noValidate="">
            <label className="form__label">
              <input
                id="input_avatar"
                className="form__input form__input_data_avatar"
                type="url"
                placeholder="Ссылка на картинку"
                name="avatar"
                required=""
              />
              <span className="form__error form__error_input_avatar" />
            </label>
            <button type="submit" className="form__button">
              Сохранить
            </button>
          </form>
        </div>
      </div>
      <template id="element-template" />
    </body>
  );
}
