import profileAvatar from '../images/profile-info__avatar.jpg';

export default function Main() {

    function handleEditAvatarClick() {
        const popupImage = document.querySelector('.popup_type_avatar');
        const buttonClose = popupImage.querySelector('.popup__close');
        buttonClose.addEventListener('click', () => {
            popupImage.classList.toggle('popup_opened');
        });
    };

    function handleEditProfileClick() {
        const popupImage = document.querySelector('.popup_type_profile');
        popupImage.classList.toggle('popup_opened');
    };

    function handleAddPlaceClick() {
        const popupImage = document.querySelector('.popup_type_place');
        popupImage.classList.toggle('popup_opened');
    };

    return (
        <>
            <main>
                <section className="profile">
                    <div className="profile-info">
                        <button
                            className="profile-info__avatar-button"
                            aria-label="изменить авата"
                            type="button"
                        />
                        <img
                            src={profileAvatar}
                            alt="аватар"
                            className="profile-info__avatar"
                        />
                        <h1 className="profile-info__title">Жак-Ив Кусто</h1>
                        <button
                            className="profile-info__edit"
                            aria-label="редактирование профиля"
                            type="button"
                        />
                        <p className="profile-info__subtitle">Исследователь океана</p>
                    </div>
                    <button
                        className="profile__add"
                        aria-label="добавить место"
                        type="button"
                    />
                </section>
                <section className="elements"></section>
            </main>
        </>
    );
}
